package com.hibees_service.service;import com.hibees_service.core.util.integrations.*;import com.hibees_service.core.util.integrations.*;import org.junit.jupiter.params.shadow.com.univocity.parsers.annotations.Headers;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Component;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Component@Headers(sequence = "Content-Type: application/json")@FeignClient(name = "infoBip-integration", url = "${integrations.info-bip.base-url}", configuration = InfoBipFeignConfiguration.class)public interface InfoBipIntegration {    @RequestMapping(method = RequestMethod.POST, value = "/email/2/send", consumes = MediaType.MULTIPART_FORM_DATA_VALUE,            produces = MediaType.APPLICATION_JSON_VALUE)    ResponseEntity<InfoBipSendEmailResponse> sendEmail(InfoBipSendEmailRequest request);    @RequestMapping(method = RequestMethod.POST, value = "/sms/2/text/advanced")    ResponseEntity<InfoBipSendSmsResponse> sendSms(InfoBipSendSmsRequest request);}